import unittest
from textwrap import dedent
from orb.misc.macaroon import Macaroon


class TestMacaroon(unittest.TestCase):
    def test_well_formatted_0(self):
        cert = Macaroon.init_from_str(
            """"""
        )
        self.assertEqual(cert.debug(), "Macaroon correctly formatted")

    def test_well_formatted_1(self):
        cert = Macaroon.init_from_str(
            """           """
        )
        self.assertEqual(cert.debug(), "Macaroon correctly formatted")

    def test_badly_formatted_0(self):
        cert = Macaroon.init_from_str(
            """       AAAAAAAAAAAAAAAAAAAAAAAAAAAAA                    """
        )
        self.assertEqual(cert.debug(), "Macaroon looks invalid")

    def test_init_from_base64_0(self):
        cert = Macaroon.init_from_base64(
            """"""
        )
        self.assertEqual(cert.debug(), "Macaroon correctly formatted")

    def test_init_from_base64_1(self):
        cert = Macaroon.init_from_base64(
            """      """
        )
        self.assertEqual(cert.debug(), "Macaroon correctly formatted")

    def test_init_from_not_sure_0(self):
        mac = Macaroon.init_from_not_sure(
            """"""
        )
        self.assertEqual(
            mac.macaroon,
            "",
        )
        self.assertEqual(mac.debug(), "Macaroon correctly formatted")

    def test_init_from_not_sure_1(self):
        cert = Macaroon.init_from_not_sure(
            """QUFBQUFBQUFBQUFBQUFBQUFBQUFB         """
        )
        self.assertEqual(cert.debug(), "Macaroon correctly formatted")


if __name__ == "__main__":
    unittest.main()
